function run(contactIdentity, tunnelOriginator) {
    try {
        const contactIdentityStart = contactIdentity.split("@")[0];
        const contactIdentityEnd = contactIdentity.split("@")[1];

        if (contactIdentityEnd === "wa.gw.msging.net") {
            return contactIdentityStart.startsWith("55")
                ? contactIdentityStart.substring(2)
                : contactIdentityStart;
        }
    } catch (error) {
        return null;
    }

    try {
        const tunnelOriginatorStart = tunnelOriginator.split("@")[0];
        const tunnelOriginatorEnd = tunnelOriginator.split("@")[1];

        if (tunnelOriginatorEnd === "wa.gw.msging.net") {
            return tunnelOriginatorStart.startsWith("55")
                ? tunnelOriginatorStart.substring(2)
                : tunnelOriginatorStart;
        }
    } catch (error) {
        return false;
    }

    return null;
