/**
 * obj = {
 *  "toHuman": true|false,
 *  "returnTo": <previous_id>,
 *  <id>: <error count>
 * }
 */

function run(id, obj, maxNumber) {
    try {
        maxNumber = Number(maxNumber);

        if(!obj) obj = {}
        else obj = JSON.parse(obj)

        obj.toHuman = false

        Object.keys(obj).forEach( function(key) {  // Reseta todos os outros.
            if(key !== id && !isNaN(parseInt(obj[key])) && !isNaN(Number(obj[key]))) obj[key] = 0
        })

        if(!obj[id]) obj[id] = 1
        else obj[id] += 1

        if(obj[id] >= maxNumber) {
            obj.toHuman = true
            obj[id] = 0
        }

        obj.returnTo = id
        obj.error = false  // Sem erros.

        return obj

    } catch (e) {
        return {
            toHuman: true,
            returnTo: '',
            error: 'Erro: ' + e
        }
    }
}

